import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/driver-dashboard.css';

const API_URL = 'http://localhost:3000/api';

function DriverDashboard() {
  const navigate = useNavigate();
  
  // State management
  const [currentUser, setCurrentUser] = useState(null);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [theme, setTheme] = useState('dark');
  const [isOnline, setIsOnline] = useState(false);
  const [availableJobs, setAvailableJobs] = useState([]);
  const [myTrips, setMyTrips] = useState([]);
  const [myBids, setMyBids] = useState([]);
  
  // Stats state
  const [stats, setStats] = useState({
    activeTrips: 0,
    completedToday: 0,
    todayEarnings: 0,
    rating: 'New'
  });

  // Modal state - FIXED
  const [showBidModal, setShowBidModal] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  
  // Form state
  const [bidAmount, setBidAmount] = useState('');
  const [bidMessage, setBidMessage] = useState('');

  // Authentication check
  useEffect(() => {
    checkAuthentication();
  }, []);

  // Initialize dashboard
  useEffect(() => {
    if (currentUser) {
      initializeDashboard();
      loadDriverData();
      
      const bidUpdateInterval = setInterval(checkForBidUpdates, 2000);
      const dataRefreshInterval = setInterval(refreshData, 60000);
      
      return () => {
        clearInterval(bidUpdateInterval);
        clearInterval(dataRefreshInterval);
      };
    }
  }, [currentUser]);

  // Update stats
  useEffect(() => {
    if (currentUser && myTrips.length >= 0) {
      updateDashboardStats();
    }
  }, [myTrips, currentUser]);

  const checkAuthentication = () => {
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');
    
    if (!token || !userData) {
      console.log('âŒ No authentication found');
      navigate('/login');
      return;
    }
    
    try {
      const user = JSON.parse(userData);
      
      if (user.role !== 'driver') {
        console.log('âŒ User is not a driver');
        navigate('/shipper-dashboard');
        return;
      }
      
      setCurrentUser(user);
      console.log('âœ… Driver authenticated:', user.name, 'ID:', user.id);
    } catch (error) {
      console.error('âŒ Error parsing user data:', error);
      localStorage.clear();
      navigate('/login');
    }
  };

  const getInitials = (name) => {
    return name?.split(' ').map(n => n[0]).join('').toUpperCase() || '??';
  };

  const initializeDashboard = () => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    setTheme(savedTheme);
    document.body.className = `${savedTheme}-theme`;
    
    const savedStatus = localStorage.getItem('driverOnlineStatus');
    if (savedStatus === 'true') {
      setIsOnline(true);
    }
  };

  const loadDriverData = async () => {
    if (!currentUser) return;
    
    try {
      console.log('ðŸ”„ Loading driver data for:', currentUser.name);
      
      const storedBids = localStorage.getItem(`driverBids_${currentUser.id}`);
      const storedTrips = localStorage.getItem(`driverTrips_${currentUser.id}`);
      
      let bids = storedBids ? JSON.parse(storedBids) : [];
      let trips = storedTrips ? JSON.parse(storedTrips) : [];
      
      try {
        const [bidsResponse, tripsResponse] = await Promise.allSettled([
          fetch(`${API_URL}/bids/driver/${currentUser.id}`, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
          }),
          fetch(`${API_URL}/trips/driver/${currentUser.id}`, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
          })
        ]);
        
        if (bidsResponse.status === 'fulfilled' && bidsResponse.value?.ok) {
          const bidsData = await bidsResponse.value.json();
          bids = bidsData.bids || [];
        }
        
        if (tripsResponse.status === 'fulfilled' && tripsResponse.value?.ok) {
          const tripsData = await tripsResponse.value.json();
          trips = tripsData.trips || [];
        }
      } catch (error) {
        console.log('ðŸŽ­ Server not available, using stored data');
      }
      
      setMyBids(bids);
      setMyTrips(trips);
      await loadAvailableJobs();
      
    } catch (error) {
      console.error('âŒ Error loading driver data:', error);
    }
  };

  const loadAvailableJobs = async () => {
    try {
      const response = await fetch(`${API_URL}/shipments/available`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
      });
      
      if (response && response.ok) {
        const data = await response.json();
        setAvailableJobs(data.shipments || []);
        console.log('âœ… Loaded jobs:', data.shipments?.length || 0);
      }
    } catch (error) {
      console.error('âŒ Error loading jobs:', error);
    }
  };

  const updateDashboardStats = () => {
    const activeTrips = myTrips.filter(t => 
      ['active', 'in_transit', 'at_destination'].includes(t.status)
    ).length;
    
    const today = new Date().toDateString();
    const completedToday = myTrips.filter(t => 
      t.status === 'completed' && new Date(t.completed_at).toDateString() === today
    ).length;
    
    const todayEarnings = myTrips
      .filter(t => t.status === 'completed' && new Date(t.completed_at).toDateString() === today)
      .reduce((sum, t) => sum + (t.final_amount || 0), 0);
    
    const ratedTrips = myTrips.filter(t => t.rating);
    const avgRating = ratedTrips.length > 0
      ? (ratedTrips.reduce((sum, t) => sum + t.rating, 0) / ratedTrips.length).toFixed(1)
      : 'New';
    
    setStats({ activeTrips, completedToday, todayEarnings, rating: avgRating });
  };

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    document.body.className = `${newTheme}-theme`;
    localStorage.setItem('theme', newTheme);
  };

  const toggleOnlineStatus = () => {
    const newStatus = !isOnline;
    setIsOnline(newStatus);
    localStorage.setItem('driverOnlineStatus', newStatus.toString());
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    navigate('/login');
  };

  const switchToSection = (section) => {
    setActiveSection(section);
    if (section === 'available-jobs') {
      loadAvailableJobs();
    }
  };

  const refreshData = () => {
    loadDriverData();
  };

  const checkForBidUpdates = async () => {
    if (!currentUser) return;
    const storedTrips = localStorage.getItem(`driverTrips_${currentUser.id}`);
    if (storedTrips) {
      const trips = JSON.parse(storedTrips);
      if (JSON.stringify(trips) !== JSON.stringify(myTrips)) {
        setMyTrips(trips);
      }
    }
  };

  // FIXED: Open bid modal
  const openBidModal = (job) => {
    console.log('ðŸ” Opening bid modal for:', job.shipment_id);
    setSelectedJob(job);
    setBidAmount('');
    setBidMessage('');
    setShowBidModal(true);
  };

  // FIXED: Close bid modal
  const closeBidModal = () => {
    setShowBidModal(false);
    setSelectedJob(null);
    setBidAmount('');
    setBidMessage('');
  };

  // FIXED: Submit bid - matches original working code
  const handleSubmitBid = async (e) => {
    e.preventDefault();
    
    if (!selectedJob) {
      alert('No job selected');
      return;
    }
    
    const amount = parseFloat(bidAmount);
    if (isNaN(amount) || amount <= 0) {
      alert('Please enter a valid bid amount');
      return;
    }

    const bidData = {
      bid_id: `BID-${Date.now()}`,
      shipment_id: selectedJob.shipment_id,
      driver_id: currentUser.id,
      driver_name: currentUser.name,
      driver_rating: currentUser.rating || 4.5,
      vehicle_type: currentUser.vehicle_type,
      vehicle_number: currentUser.vehicle_number,
      license_number: currentUser.license_number,
      bid_amount: amount,
      message: bidMessage || '',
      status: 'pending',
      created_at: new Date().toISOString()
    };

    try {
      console.log('ðŸ“¤ Submitting bid:', bidData);
      
      const response = await fetch(`${API_URL}/bids`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          shipment_id: selectedJob.shipment_id,
          bid_amount: amount,
          message: bidMessage || ''
        })
      });

      if (response.ok) {
        const data = await response.json();
        console.log('âœ… Bid submitted:', data);
        
        // Store locally too
        storeBidLocally(bidData);
        setMyBids(prev => [...prev, bidData]);
        
        alert('Bid submitted successfully!');
        closeBidModal();
        loadAvailableJobs();
      } else {
        const error = await response.json();
        alert(error.error || 'Failed to submit bid');
      }
    } catch (error) {
      console.error('âŒ Error:', error);
      // Fallback to local storage
      storeBidLocally(bidData);
      setMyBids(prev => [...prev, bidData]);
      alert('Bid submitted (offline mode)');
      closeBidModal();
    }
  };

  const storeBidLocally = (bidData) => {
    const allBids = JSON.parse(localStorage.getItem('allBids') || '[]');
    allBids.push(bidData);
    localStorage.setItem('allBids', JSON.stringify(allBids));
    localStorage.setItem(`driverBids_${currentUser.id}`, JSON.stringify([...myBids, bidData]));
  };

  const getStatusBadge = (status) => {
    const badges = {
      pending: <span className="badge badge-warning">Pending</span>,
      active: <span className="badge badge-info">Active</span>,
      in_transit: <span className="badge badge-primary">In Transit</span>,
      completed: <span className="badge badge-success">Completed</span>
    };
    return badges[status] || <span className="badge">{status}</span>;
  };

  if (!currentUser) {
    return <div className="loading-spinner"><i className="fas fa-spinner fa-spin"></i></div>;
  }

  return (
    <>
      {/* Header */}
      <header className="header">
        <div className="logo">
          <i className="fas fa-truck"></i>
          CargoMate
        </div>
        <div className="header-actions">
          <div className="user-info">
            <div className="user-avatar">{getInitials(currentUser.name)}</div>
            <div className="user-details">
              <h3>{currentUser.name}</h3>
              <p>Driver</p>
            </div>
          </div>
          <button className="logout-btn" onClick={logout}>
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
          <button className="theme-toggle" onClick={toggleTheme}>
            <i className={`fas fa-${theme === 'dark' ? 'sun' : 'moon'}`}></i>
          </button>
        </div>
      </header>

      {/* Dashboard Layout */}
      <div className="dashboard-layout">
        {/* Sidebar */}
        <nav className="sidebar">
          {['dashboard', 'my-trips', 'available-jobs', 'vehicle-status', 'earnings', 'performance', 'settings', 'support'].map(section => (
            <a
              key={section}
              href="#"
              className={`nav-item ${activeSection === section ? 'active' : ''}`}
              onClick={(e) => { e.preventDefault(); switchToSection(section); }}
            >
              <i className={`fas fa-${
                section === 'dashboard' ? 'tachometer-alt' :
                section === 'my-trips' ? 'route' :
                section === 'available-jobs' ? 'search' :
                section === 'vehicle-status' ? 'truck' :
                section === 'earnings' ? 'rupee-sign' :
                section === 'performance' ? 'chart-line' :
                section === 'settings' ? 'cog' : 'life-ring'
              }`}></i>
              {section.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}
            </a>
          ))}
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {/* Dashboard Section */}
          {activeSection === 'dashboard' && (
            <div className="section active">
              <div className="section-header">
                <h1>Driver Dashboard</h1>
                <div className="header-buttons">
                  <button 
                    className={`btn ${isOnline ? 'btn-warning' : 'btn-primary'}`}
                    onClick={toggleOnlineStatus}
                  >
                    <i className={`fas fa-${isOnline ? 'pause' : 'play'}`}></i>
                    {isOnline ? 'Go Offline' : 'Go Online'}
                  </button>
                  <button className="btn btn-secondary" onClick={() => switchToSection('available-jobs')}>
                    <i className="fas fa-search"></i> Find Jobs
                  </button>
                </div>
              </div>

              {/* Stats Grid */}
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon green"><i className="fas fa-route"></i></div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.activeTrips}</div>
                    <div className="stat-label">Active Trips</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon blue"><i className="fas fa-check-circle"></i></div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.completedToday}</div>
                    <div className="stat-label">Completed Today</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon purple"><i className="fas fa-rupee-sign"></i></div>
                  <div className="stat-info">
                    <div className="stat-value">â‚¹{stats.todayEarnings.toLocaleString()}</div>
                    <div className="stat-label">Today's Earnings</div>
                  </div>
                </div>
                <div className="stat-card">
                  <div className="stat-icon orange"><i className="fas fa-star"></i></div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.rating}</div>
                    <div className="stat-label">Rating</div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Available Jobs Section */}
          {activeSection === 'available-jobs' && (
            <div className="section active">
              <div className="section-header">
                <h1>Available Jobs</h1>
                <button className="btn btn-secondary" onClick={loadAvailableJobs}>
                  <i className="fas fa-sync"></i> Refresh
                </button>
              </div>
              
              <div className="jobs-grid">
                {availableJobs.length > 0 ? (
                  availableJobs.map(job => (
                    <div key={job._id || job.id} className="job-card">
                      <div className="job-header">
                        <h3>#{job.shipment_id}</h3>
                        <span className="badge badge-warning">Open</span>
                      </div>
                      <div className="job-body">
                        <p><strong>Route:</strong> {job.from_location} â†’ {job.to_location}</p>
                        <p><strong>Package:</strong> {job.package_type} ({job.package_weight}kg)</p>
                        <p><strong>Pickup:</strong> {new Date(job.pickup_date).toLocaleDateString()}</p>
                      </div>
                      <div className="job-footer">
                        <button className="btn btn-primary" onClick={() => openBidModal(job)}>
                          <i className="fas fa-gavel"></i> Place Bid
                        </button>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="empty-state">
                    <i className="fas fa-search"></i>
                    <h3>No Jobs Available</h3>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* My Trips Section */}
          {activeSection === 'my-trips' && (
            <div className="section active">
              <div className="section-header">
                <h1>My Trips</h1>
              </div>
              <div className="content-card">
                {myTrips.length > 0 ? (
                  <div className="trips-grid">
                    {myTrips.map(trip => (
                      <div key={trip.id} className="trip-card">
                        <h3>{trip.shipment_id}</h3>
                        {getStatusBadge(trip.status)}
                        <p>{trip.from_location} â†’ {trip.to_location}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <i className="fas fa-clipboard-list"></i>
                    <h3>No Trips Yet</h3>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Other sections */}
          {['vehicle-status', 'earnings', 'performance', 'settings', 'support'].includes(activeSection) && (
            <div className="section active">
              <div className="section-header">
                <h1>{activeSection.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}</h1>
              </div>
              <div className="content-card">
                <p>This section is under development.</p>
              </div>
            </div>
          )}
        </main>
      </div>

      {/* FIXED: Bid Modal */}
      {showBidModal && selectedJob && (
        <div className="modal-overlay" onClick={closeBidModal} style={{display: 'flex'}}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Submit Your Bid</h3>
              <button className="modal-close" onClick={closeBidModal}>Ã—</button>
            </div>
            <div className="modal-body">
              <div className="bid-job-info" style={{marginBottom: '20px', padding: '15px', background: '#2a2a2a', borderRadius: '8px'}}>
                <h4>#{selectedJob.shipment_id}</h4>
                <p>{selectedJob.from_location} â†’ {selectedJob.to_location}</p>
                <p><strong>Package:</strong> {selectedJob.package_type} ({selectedJob.package_weight}kg)</p>
              </div>
              <form onSubmit={handleSubmitBid}>
                <div className="form-group">
                  <label htmlFor="bidAmount">Your Bid Amount (â‚¹) *</label>
                  <input
                    type="number"
                    id="bidAmount"
                    className="form-input"
                    placeholder="Enter your bid amount"
                    value={bidAmount}
                    onChange={(e) => setBidAmount(e.target.value)}
                    required
                    min="100"
                    step="10"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="bidMessage">Message (Optional)</label>
                  <textarea
                    id="bidMessage"
                    className="form-input"
                    rows="3"
                    placeholder="Tell the customer why they should choose you..."
                    value={bidMessage}
                    onChange={(e) => setBidMessage(e.target.value)}
                  ></textarea>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" onClick={closeBidModal}>
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    <i className="fas fa-paper-plane"></i> Submit Bid
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default DriverDashboard;
