import React, { useEffect, useState } from "react";

function Billing({ userId }) {
  const [completedTrips, setCompletedTrips] = useState([]);

  useEffect(() => {
    fetchCompletedUnpaid();
  }, []);

  const fetchCompletedUnpaid = async () => {
    const token = localStorage.getItem('authToken');
    const res = await fetch('http://localhost:3000/api/shipments/user/' + userId, {
      headers: { 'Authorization': 'Bearer ' + token }
    });
    if (res.ok) {
      const data = await res.json();
      const eligible = (data.shipments || []).filter(t =>
        t.status === "completed" && t.payment_status !== "paid"
      );
      setCompletedTrips(eligible);
    }
  };

  const payForShipment = async (shipmentId, tripId) => {
    const token = localStorage.getItem('authToken');
    const res = await fetch('http://localhost:3000/api/payments/mark-paid', {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ shipment_id: shipmentId, trip_id: tripId })
    });
    if (res.ok) {
      alert("Payment successful!");
      fetchCompletedUnpaid();
    }
  };

  return (
    <div>
      {completedTrips.length === 0 && <p>No completed jobs pending payment.</p>}
      {completedTrips.map(trip => (
        <div className="billing-row" key={trip.shipment_id}>
          <span>Shipment #{trip.shipment_id}</span>
          <span>Amount: â‚¹{trip.bid_amount}</span>
          <button onClick={() => payForShipment(trip.shipment_id, trip.trip_id)}>
            Pay Now
          </button>
        </div>
      ))}
    </div>
  );
}

export default Billing;
