import React, { useState, useEffect, useRef } from 'react';
import '../styles/tracking-modal.css';

function TrackingModal({ shipment, driver, onClose }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Load messages on mount and auto-refresh
  useEffect(() => {
    if (shipment) {
      loadMessages();
      const interval = setInterval(loadMessages, 2000);
      return () => clearInterval(interval);
    }
  }, [shipment]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const loadMessages = async () => {
    try {
      const response = await fetch(
        `http://localhost:3000/api/messages/${shipment.shipment_id}`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`
          }
        }
      );
      
      if (response.ok) {
        const data = await response.json();
        console.log('üì• Loaded messages:', data.messages);
        setMessages(data.messages || []);
      }
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    
    if (!newMessage.trim()) {
      alert('Please type a message');
      return;
    }

    setLoading(true);
    try {
      console.log('üì§ Sending message:', newMessage);
      
      const response = await fetch(
        `http://localhost:3000/api/messages/${shipment.shipment_id}`,
        {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            message: newMessage.trim(),
            sender_role: 'shipper'
          })
        }
      );

      const data = await response.json();
      
      if (response.ok) {
        console.log('‚úÖ Message sent:', data);
        setNewMessage('');
        // Reload messages immediately
        setTimeout(loadMessages, 100);
      } else {
        alert('Failed to send message: ' + data.error);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      alert('Error sending message: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="tracking-modal-overlay" onClick={onClose}>
      <div className="tracking-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="tracking-modal-header">
          <h2>üí¨ Chat with Driver</h2>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="tracking-container">
          {/* Tracking Info */}
          <div className="tracking-section">
            <h3>üìç Shipment Info</h3>
            <div className="shipment-info">
              <div className="info-row">
                <span className="label">Shipment:</span>
                <span className="value">{shipment.shipment_id}</span>
              </div>
              <div className="info-row">
                <span className="label">Route:</span>
                <span className="value">{shipment.from_location} ‚Üí {shipment.to_location}</span>
              </div>
              <div className="info-row">
                <span className="label">Driver:</span>
                <span className="value">{driver?.name || 'Not assigned'}</span>
              </div>
              <div className="info-row">
                <span className="label">Status:</span>
                <span className={`status-badge ${shipment.status}`}>{shipment.status?.toUpperCase()}</span>
              </div>
            </div>

            <div className="live-map">
              <div className="map-placeholder">
                <div className="map-icon">üó∫Ô∏è</div>
                <p>Live tracking map</p>
                <div className="tracking-badge">
                  <div className="pulse"></div>
                  Live
                </div>
              </div>
            </div>
          </div>

          {/* Chat Section */}
          <div className="chat-section">
            <h3>üí¨ Chat with Driver</h3>
            
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="no-messages">No messages yet. Start the conversation!</div>
              ) : (
                <>
                  {messages.map((msg, idx) => (
                    <div key={idx} className={`message ${msg.sender_role}`}>
                      <div className="message-content">
                        <p>{msg.message}</p>
                        <small>
                          {new Date(msg.timestamp).toLocaleTimeString()}
                        </small>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </>
              )}
            </div>

            <form onSubmit={sendMessage} className="message-form">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
                disabled={loading}
              />
              <button type="submit" disabled={loading}>
                {loading ? '‚è≥' : 'üì§'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TrackingModal;
