import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/driver-dashboard.css';

function DriverDashboard() {
  const navigate = useNavigate();
  
  // State management
  const [currentUser, setCurrentUser] = useState(null);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [theme, setTheme] = useState('dark');
  const [isOnline, setIsOnline] = useState(false);
  const [availableJobs, setAvailableJobs] = useState([]);
  const [myTrips, setMyTrips] = useState([]);
  const [myBids, setMyBids] = useState([]);
  const [currentTripId, setCurrentTripId] = useState(null);
  
  // Stats state
  const [stats, setStats] = useState({
    activeTrips: 0,
    completedToday: 0,
    todayEarnings: 0,
    rating: 'New'
  });

  // Modal state
  const [modalState, setModalState] = useState({
    bidModal: false,
    tripDetailModal: false,
    chatModal: false,
    selectedJob: null,
    selectedTrip: null
  });

  // Form state
  const [bidForm, setBidForm] = useState({
    amount: '',
    message: ''
  });

  // Authentication check
  useEffect(() => {
    checkAuthentication();
  }, []);

  // Initialize dashboard after authentication
  useEffect(() => {
    if (currentUser) {
      initializeDashboard();
      loadDriverData();
      
      // Set up intervals for real-time updates
      const bidUpdateInterval = setInterval(checkForBidUpdates, 2000);
      const dataRefreshInterval = setInterval(refreshData, 60000);
      
      return () => {
        clearInterval(bidUpdateInterval);
        clearInterval(dataRefreshInterval);
      };
    }
  }, [currentUser]);

  // Update stats whenever trips change
  useEffect(() => {
    if (currentUser && myTrips.length >= 0) {
      updateDashboardStats();
    }
  }, [myTrips, currentUser]);

  const checkAuthentication = () => {
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');
    
    if (!token || !userData) {
      console.log('âŒ No authentication found');
      navigate('/login');
      return;
    }
    
    try {
      const user = JSON.parse(userData);
      
      if (user.role !== 'driver') {
        console.log('âŒ User is not a driver');
        navigate('/shipper-dashboard');
        return;
      }
      
      setCurrentUser(user);
      console.log('âœ… Driver authenticated:', user.name, 'ID:', user.id);
    } catch (error) {
      console.error('âŒ Error parsing user data:', error);
      localStorage.clear();
      navigate('/login');
    }
  };

  const getInitials = (name) => {
    return name?.split(' ').map(n => n[0]).join('').toUpperCase() || '??';
  };

  const initializeDashboard = () => {
    // Load theme preference
    const savedTheme = localStorage.getItem('theme') || 'dark';
    setTheme(savedTheme);
    document.body.className = `${savedTheme}-theme`;
    
    // Load online status
    const savedStatus = localStorage.getItem('driverOnlineStatus');
    if (savedStatus === 'true') {
      setIsOnline(true);
    }
  };

  const loadDriverData = async () => {
    if (!currentUser) return;
    
    try {
      console.log('ðŸ”„ Loading driver data for:', currentUser.name, 'ID:', currentUser.id);
      
      // Load stored data
      const storedBids = localStorage.getItem(`driverBids_${currentUser.id}`);
      const storedTrips = localStorage.getItem(`driverTrips_${currentUser.id}`);
      
      let bids = storedBids ? JSON.parse(storedBids) : [];
      let trips = storedTrips ? JSON.parse(storedTrips) : [];
      
      console.log('ðŸ“Š Loaded from localStorage:', { bids: bids.length, trips: trips.length });
      
      // Try server connections
      try {
        const [bidsResponse, tripsResponse] = await Promise.allSettled([
          fetch(`http://localhost:3000/api/bids/driver/${currentUser.id}`, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
          }).catch(() => null),
          fetch(`http://localhost:3000/api/trips/driver/${currentUser.id}`, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
          }).catch(() => null)
        ]);
        
        if (bidsResponse.status === 'fulfilled' && bidsResponse.value?.ok) {
          const bidsData = await bidsResponse.value.json();
          bids = bidsData.bids || [];
          console.log('âœ… Loaded bids from server:', bids.length);
        }
        
        if (tripsResponse.status === 'fulfilled' && tripsResponse.value?.ok) {
          const tripsData = await tripsResponse.value.json();
          trips = tripsData.trips || [];
          console.log('âœ… Loaded trips from server:', trips.length);
        }
      } catch (error) {
        console.log('ðŸŽ­ Server not available, using stored data');
      }
      
      setMyBids(bids);
      setMyTrips(trips);
      
      // Load available jobs
      await loadAvailableJobs();
      
    } catch (error) {
      console.error('âŒ Error loading driver data:', error);
      setMyBids([]);
      setMyTrips([]);
    }
  };

  const saveDriverData = useCallback(() => {
    if (!currentUser) return;
    
    const bidsKey = `driverBids_${currentUser.id}`;
    const tripsKey = `driverTrips_${currentUser.id}`;
    
    localStorage.setItem(bidsKey, JSON.stringify(myBids));
    localStorage.setItem(tripsKey, JSON.stringify(myTrips));
    
    console.log('ðŸ’¾ Saved driver data:', { bidsKey, tripsKey, bidsCount: myBids.length, tripsCount: myTrips.length });
  }, [currentUser, myBids, myTrips]);

  useEffect(() => {
    if (currentUser) {
      saveDriverData();
    }
  }, [myBids, myTrips, saveDriverData, currentUser]);

  const loadAvailableJobs = async () => {
    try {
      console.log('ðŸ”„ Loading available jobs...');
      
      const response = await fetch('http://localhost:3000/api/shipments/available', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
      }).catch(() => null);
      
      if (response && response.ok) {
        const data = await response.json();
        setAvailableJobs(data.shipments || []);
        console.log('âœ… Loaded jobs from server:', data.shipments?.length || 0);
      } else {
        console.log('ðŸŽ­ Using demo jobs');
        setAvailableJobs(getDemoJobs());
      }
    } catch (error) {
      console.error('âŒ Error loading available jobs:', error);
      setAvailableJobs(getDemoJobs());
    }
  };

  const getDemoJobs = () => {
    const allShipments = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key && key.startsWith('demoShipments_')) {
        try {
          const shipments = JSON.parse(localStorage.getItem(key));
          allShipments.push(...shipments.filter(s => s.status === 'pending_bids'));
        } catch (e) {
          console.log('Error parsing shipment data:', e);
        }
      }
    }
    return allShipments;
  };

  const updateDashboardStats = () => {
    const activeTrips = myTrips.filter(t => 
      t.status === 'active' || t.status === 'in_transit' || t.status === 'at_destination'
    ).length;
    
    const completedToday = myTrips.filter(t => {
      const today = new Date().toDateString();
      return t.status === 'completed' && new Date(t.completed_at).toDateString() === today;
    }).length;
    
    const todayEarnings = myTrips
      .filter(t => {
        const today = new Date().toDateString();
        return t.status === 'completed' && new Date(t.completed_at).toDateString() === today;
      })
      .reduce((sum, t) => sum + (t.final_amount || 0), 0);
    
    const ratedTrips = myTrips.filter(t => t.rating);
    const avgRating = ratedTrips.length > 0
      ? (ratedTrips.reduce((sum, t) => sum + t.rating, 0) / ratedTrips.length).toFixed(1)
      : 'New';
    
    setStats({
      activeTrips,
      completedToday,
      todayEarnings,
      rating: avgRating
    });
  };

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    document.body.className = `${newTheme}-theme`;
    localStorage.setItem('theme', newTheme);
  };

  const toggleOnlineStatus = () => {
    const newStatus = !isOnline;
    setIsOnline(newStatus);
    localStorage.setItem('driverOnlineStatus', newStatus.toString());
    console.log(newStatus ? 'âœ… Driver is now ONLINE' : 'â¸ï¸ Driver is now OFFLINE');
  };

  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    navigate('/login');
  };

  const switchToSection = (section) => {
    setActiveSection(section);
    
    if (section === 'available-jobs') {
      loadAvailableJobs();
    } else if (section === 'my-trips') {
      // Trips already loaded
    }
  };

  const refreshData = () => {
    loadDriverData();
    console.log('ðŸ”„ Data refreshed');
  };

  const checkForBidUpdates = async () => {
    // Check for bid status updates from server or localStorage
    if (!currentUser) return;
    
    const storedTrips = localStorage.getItem(`driverTrips_${currentUser.id}`);
    if (storedTrips) {
      const trips = JSON.parse(storedTrips);
      if (JSON.stringify(trips) !== JSON.stringify(myTrips)) {
        setMyTrips(trips);
        console.log('ðŸ”” Trip updates detected');
      }
    }
  };

  // Modal handlers - FIXED VERSION
  const openBidModal = (job) => {
    console.log('ðŸ” Opening bid modal for job:', job);
    console.log('ðŸ” Job shipment_id:', job.shipment_id);
    
    // Use functional update to ensure proper state update
    setModalState(prev => ({
      ...prev,
      bidModal: true,
      selectedJob: job
    }));
    
    setBidForm({
      amount: '',
      message: ''
    });
  };

  const closeBidModal = () => {
    setModalState(prev => ({
      ...prev,
      bidModal: false,
      selectedJob: null
    }));
    
    setBidForm({
      amount: '',
      message: ''
    });
  };

  const handleSubmitBid = async (e) => {
    e.preventDefault();
    
    if (!modalState.selectedJob) {
      alert('No job selected');
      return;
    }
    
    const bidAmount = parseFloat(bidForm.amount);
    if (isNaN(bidAmount) || bidAmount <= 0) {
      alert('Please enter a valid bid amount');
      return;
    }

    try {
      console.log('Submitting bid for shipment:', modalState.selectedJob.shipment_id);
      
      const response = await fetch('http://localhost:3000/api/bids', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          shipment_id: modalState.selectedJob.shipment_id,
          bid_amount: bidAmount,
          message: bidForm.message || ''
        })
      });

      if (response.ok) {
        const data = await response.json();
        console.log('âœ… Bid submitted successfully:', data);
        
        // Add to local bids list
        setMyBids(prev => [...prev, data.bid]);
        
        closeBidModal();
        alert('Bid submitted successfully!');
        
        // Refresh available jobs
        loadAvailableJobs();
      } else {
        const error = await response.json();
        console.error('âŒ Bid submission failed:', error);
        alert(error.error || 'Failed to submit bid');
      }
    } catch (error) {
      console.error('âŒ Error submitting bid:', error);
      alert('Network error. Please check your connection.');
    }
  };

  const getStatusBadge = (status) => {
    const badges = {
      pending: <span className="badge badge-warning">Pending</span>,
      active: <span className="badge badge-info">Active</span>,
      in_transit: <span className="badge badge-primary">In Transit</span>,
      completed: <span className="badge badge-success">Completed</span>,
      cancelled: <span className="badge badge-error">Cancelled</span>
    };
    return badges[status] || <span className="badge">{status}</span>;
  };

  if (!currentUser) {
    return <div className="loading-spinner"><i className="fas fa-spinner fa-spin"></i></div>;
  }

  return (
    <>
      {/* Header */}
      <header className="header">
        <div className="logo">
          <i className="fas fa-truck"></i>
          CargoMate
        </div>
        <div className="header-actions">
          <div className="user-info">
            <div className="user-avatar">
              {getInitials(currentUser.name)}
            </div>
            <div className="user-details">
              <h3>{currentUser.name}</h3>
              <p>Driver</p>
            </div>
          </div>
          <button className="logout-btn" onClick={logout}>
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
          <button className="theme-toggle" onClick={toggleTheme}>
            <i className={`fas fa-${theme === 'dark' ? 'sun' : 'moon'}`}></i>
          </button>
        </div>
      </header>

      {/* Dashboard Layout */}
      <div className="dashboard-layout">
        {/* Sidebar Navigation */}
        <nav className="sidebar">
          {['dashboard', 'my-trips', 'available-jobs', 'vehicle-status', 'earnings', 'performance', 'settings', 'support'].map(section => (
            <a
              key={section}
              href="#"
              className={`nav-item ${activeSection === section ? 'active' : ''}`}
              onClick={(e) => { e.preventDefault(); switchToSection(section); }}
              data-section={section}
            >
              <i className={`fas fa-${
                section === 'dashboard' ? 'tachometer-alt' :
                section === 'my-trips' ? 'route' :
                section === 'available-jobs' ? 'search' :
                section === 'vehicle-status' ? 'truck' :
                section === 'earnings' ? 'rupee-sign' :
                section === 'performance' ? 'chart-line' :
                section === 'settings' ? 'cog' : 'life-ring'
              }`}></i>
              {section.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}
            </a>
          ))}
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {/* Dashboard Section */}
          {activeSection === 'dashboard' && (
            <div className="section active">
              <div className="section-header">
                <h1>Driver Dashboard</h1>
                <div className="header-buttons">
                  <button 
                    className={`btn ${isOnline ? 'btn-warning' : 'btn-primary'}`}
                    onClick={toggleOnlineStatus}
                  >
                    <i className={`fas fa-${isOnline ? 'pause' : 'play'}`}></i>
                    <span>{isOnline ? 'Go Offline' : 'Go Online'}</span>
                  </button>
                  <button className="btn btn-secondary" onClick={() => switchToSection('available-jobs')}>
                    <i className="fas fa-search"></i>
                    Find Jobs
                  </button>
                </div>
              </div>

              {/* Stats Grid */}
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon green">
                    <i className="fas fa-route"></i>
                  </div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.activeTrips}</div>
                    <div className="stat-label">Active Trips</div>
                    <div className="stat-change">
                      {stats.activeTrips > 0 ? `${stats.activeTrips} trips in progress` : 'Start accepting jobs'}
                    </div>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon blue">
                    <i className="fas fa-check-circle"></i>
                  </div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.completedToday}</div>
                    <div className="stat-label">Completed Today</div>
                    <div className="stat-change">
                      {stats.completedToday > 0 ? 'Great job today!' : 'No trips completed today'}
                    </div>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon purple">
                    <i className="fas fa-rupee-sign"></i>
                  </div>
                  <div className="stat-info">
                    <div className="stat-value">â‚¹{stats.todayEarnings.toLocaleString()}</div>
                    <div className="stat-label">Today's Earnings</div>
                    <div className="stat-change">
                      {stats.todayEarnings > 0 ? "Today's earnings" : 'Complete trips to earn'}
                    </div>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon orange">
                    <i className="fas fa-star"></i>
                  </div>
                  <div className="stat-info">
                    <div className="stat-value">{stats.rating}</div>
                    <div className="stat-label">Driver Rating</div>
                    <div className="stat-change">
                      {stats.rating !== 'New' ? `Based on ${myTrips.filter(t => t.rating).length} trips` : 'Complete trips to get rated'}
                    </div>
                  </div>
                </div>
              </div>

              {/* Current Trips */}
              <div className="content-card">
                <div className="card-header">
                  <h2>Current Trips</h2>
                  <button className="btn btn-link" onClick={refreshData}>Refresh</button>
                </div>
                <div className="table-container">
                  {myTrips.filter(t => t.status === 'active' || t.status === 'in_transit').length > 0 ? (
                    <table className="trips-table">
                      <thead>
                        <tr>
                          <th>Trip ID</th>
                          <th>Route</th>
                          <th>Customer</th>
                          <th>Status</th>
                          <th>Amount</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {myTrips.filter(t => t.status === 'active' || t.status === 'in_transit').map(trip => (
                          <tr key={trip.id}>
                            <td>#{trip.shipment_id}</td>
                            <td>{trip.from_location} â†’ {trip.to_location}</td>
                            <td>{trip.shipper_name}</td>
                            <td>{getStatusBadge(trip.status)}</td>
                            <td>â‚¹{trip.final_amount.toLocaleString()}</td>
                            <td>
                              <button className="btn btn-sm btn-primary">View Details</button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  ) : (
                    <div className="empty-state">
                      <i className="fas fa-route"></i>
                      <h3>No Active Trips</h3>
                      <p>Find available jobs and start earning</p>
                      <button className="btn btn-primary" onClick={() => switchToSection('available-jobs')}>
                        Find Jobs
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Available Jobs Section */}
          {activeSection === 'available-jobs' && (
            <div className="section active">
              <div className="section-header">
                <h1>Available Jobs</h1>
                <p>Find and bid on shipments that match your vehicle</p>
              </div>
              
              <div className="jobs-grid">
                {availableJobs.length > 0 ? (
                  availableJobs.map(job => (
                    <div key={job._id ||job.id} className="job-card">
                      <div className="job-header">
                        <h3>#{job.shipment_id}</h3>
                        <span className="badge badge-warning">Open for Bids</span>
                      </div>
                      <div className="job-body">
                        <div className="job-route">
                          <i className="fas fa-map-marker-alt"></i>
                          <div>
                            <strong>From:</strong> {job.from_location}<br />
                            <strong>To:</strong> {job.to_location}
                          </div>
                        </div>
                        <div className="job-details">
                          <p><strong>Package:</strong> {job.package_type} ({job.package_weight}kg)</p>
                          <p><strong>Pickup:</strong> {new Date(job.pickup_date).toLocaleDateString()}</p>
                          <p>{job.package_description}</p>
                        </div>
                      </div>
                      <div className="job-footer">
                        <button className="btn btn-primary" onClick={() => openBidModal(job)}>
                          <i className="fas fa-hand-holding-usd"></i>
                          Place Bid
                        </button>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="empty-state">
                    <i className="fas fa-search"></i>
                    <h3>No Jobs Available</h3>
                    <p>Check back later for new shipments</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* My Trips Section */}
          {activeSection === 'my-trips' && (
            <div className="section active">
              <div className="section-header">
                <h1>My Trips</h1>
                <p>View and manage all your trips</p>
              </div>
              
              <div className="content-card">
                {myTrips.length > 0 ? (
                  <div className="trips-grid">
                    {myTrips.map(trip => (
                      <div key={trip.id} className="trip-card">
                        <div className="trip-header">
                          <h3>Trip #{trip.shipment_id}</h3>
                          {getStatusBadge(trip.status)}
                        </div>
                        <div className="trip-body">
                          <p><strong>Route:</strong> {trip.from_location} â†’ {trip.to_location}</p>
                          <p><strong>Customer:</strong> {trip.shipper_name}</p>
                          <p><strong>Package:</strong> {trip.package_type} ({trip.package_weight}kg)</p>
                          <p><strong>Amount:</strong> â‚¹{trip.final_amount.toLocaleString()}</p>
                          {trip.rating && (
                            <p><strong>Rating:</strong> {'â­'.repeat(trip.rating)} ({trip.rating}/5)</p>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <i className="fas fa-clipboard-list"></i>
                    <h3>No Trips Yet</h3>
                    <p>Accept jobs to see your trips here</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Placeholder sections for other views */}
          {['vehicle-status', 'earnings', 'performance', 'settings', 'support'].includes(activeSection) && (
            <div className="section active">
              <div className="section-header">
                <h1>{activeSection.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}</h1>
              </div>
              <div className="content-card">
                <p>This section is under development.</p>
              </div>
            </div>
          )}
        </main>
      </div>

      {/* Bid Modal - FIXED */}
      {modalState.bidModal && modalState.selectedJob && (
        <div className="modal-overlay" onClick={closeBidModal}>
          <div className="modal-content bid-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Submit Your Bid</h3>
              <button className="close-modal" onClick={closeBidModal}>Ã—</button>
            </div>
            <div className="modal-body">
              <div className="bid-job-info">
                <h4>#{modalState.selectedJob.shipment_id}</h4>
                <p>{modalState.selectedJob.from_location} â†’ {modalState.selectedJob.to_location}</p>
                <p><strong>Package:</strong> {modalState.selectedJob.package_type} ({modalState.selectedJob.package_weight}kg)</p>
              </div>
              <form onSubmit={handleSubmitBid}>
                <div className="form-group">
                  <label htmlFor="bidAmount">Your Bid Amount (â‚¹) *</label>
                  <input
                    type="number"
                    id="bidAmount"
                    className="form-input"
                    placeholder="Enter your bid amount"
                    value={bidForm.amount}
                    onChange={(e) => setBidForm(prev => ({ ...prev, amount: e.target.value }))}
                    required
                    min="1"
                    step="1"
                  />
                  <small>Enter a competitive amount to increase your chances</small>
                </div>
                <div className="form-group">
                  <label htmlFor="bidMessage">Message to Customer (Optional)</label>
                  <textarea
                    id="bidMessage"
                    className="form-input"
                    rows="3"
                    placeholder="Tell them why they should choose you..."
                    value={bidForm.message}
                    onChange={(e) => setBidForm(prev => ({ ...prev, message: e.target.value }))}
                  ></textarea>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" onClick={closeBidModal}>
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    <i className="fas fa-paper-plane"></i>
                    Submit Bid
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default DriverDashboard;
