import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/shipper-dashboard.css';

function ShipperDashboard() {
  const navigate = useNavigate();

  const [currentUser, setCurrentUser] = useState(null);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [theme, setTheme] = useState('dark');
  const [userShipments, setUserShipments] = useState([]);
  const [filteredShipments, setFilteredShipments] = useState([]);
  const [statusFilter, setStatusFilter] = useState('');
  const [modalState, setModalState] = useState({
    bidsModal: false,
    selectedShipment: null,
    currentBids: []
  });

  // Authentication and load user data on mount
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');
    if (!token || !userData) {
      navigate('/login');
      return;
    }
    try {
      const user = JSON.parse(userData);
      if (user.role !== 'shipper') {
        navigate('/driver-dashboard');
        return;
      }
      setCurrentUser(user);
    } catch {
      localStorage.clear();
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    if (currentUser) {
      loadShipments();
    }
  }, [currentUser]);

  const loadShipments = async () => {
    try {
      const response = await fetch(`http://localhost:3000/api/shipments/user/${currentUser.id}`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
      });
      if (response.ok) {
        const data = await response.json();
        setUserShipments(data.shipments || []);
        setFilteredShipments(data.shipments || []);
      }
    } catch (error) {
      setUserShipments([]);
      setFilteredShipments([]);
    }
  };

  // Handle View Bids click
  const viewBids = async (shipment) => {
    try {
      const response = await fetch(`http://localhost:3000/api/shipments/${shipment.shipment_id}/bids`, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('authToken')}` }
      });
      if (response.ok) {
        const data = await response.json();
        setModalState({
          bidsModal: true,
          selectedShipment: shipment,
          currentBids: data.bids || []
        });
      } else {
        setModalState({
          bidsModal: true,
          selectedShipment: shipment,
          currentBids: []
        });
      }
    } catch {
      alert('Failed to load bids.');
    }
  };

  // Close Bids modal
  const closeBidsModal = () => {
    setModalState({
      bidsModal: false,
      selectedShipment: null,
      currentBids: []
    });
  };

  // On filter status change
  const filterShipmentsByStatus = (status) => {
    setStatusFilter(status);
    if (!status) setFilteredShipments(userShipments);
    else setFilteredShipments(userShipments.filter(s => s.status === status));
  };

  // Utility for unique key
  const getKey = (item, index) => item.bid_id || item.id || index;

  if (!currentUser) {
    return <div className="loading-spinner"><i className="fas fa-spinner fa-spin"></i></div>;
  }

  return (
    <>
      <header className="header">
        <div className="logo"><i className="fas fa-truck"></i> CargoMate</div>
        <div className="header-actions">
          <div className="user-info">
            <div className="user-avatar">{currentUser.name.split(' ').map(n => n[0]).join('').toUpperCase()}</div>
            <div className="user-details">
              <h3>{currentUser.name}</h3>
              <p>Shipper</p>
            </div>
          </div>
          <button onClick={() => { localStorage.clear(); navigate('/login'); }} className="logout-btn">
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
      </header>

      <main className="main-content">
        <section className={`section ${activeSection === 'my-shipments' ? 'active' : ''}`}>
          <h1>My Shipments</h1>
          <select value={statusFilter} onChange={e => filterShipmentsByStatus(e.target.value)}>
            <option value="">All Status</option>
            <option value="pending_bids">Pending Bids</option>
            <option value="active">Active</option>
            <option value="in_transit">In Transit</option>
            <option value="delivered">Delivered</option>
            <option value="completed">Completed</option>
          </select>
          <div className="shipments-list">
            {filteredShipments.length > 0 ? filteredShipments.map((shipment, index) => (
              <div key={shipment.shipment_id || shipment.id || index} className="shipment-card">
                <h3>#{shipment.shipment_id}</h3>
                <p><b>Route:</b> {shipment.from_location} → {shipment.to_location}</p>
                <p><b>Package:</b> {shipment.package_type} ({shipment.package_weight}kg)</p>
                <p><b>Status:</b> {shipment.status}</p>
                <p><b>Bids:</b> {shipment.bids_count || 0}</p>
                {shipment.status === 'pending_bids' && (
                  <button onClick={() => viewBids(shipment)}>View Bids</button>
                )}
              </div>
            )) : (<p>No shipments found</p>)}
          </div>
        </section>

        {/* Bids Modal */}
        {modalState.bidsModal && (
          <div className="modal-overlay" onClick={closeBidsModal}>
            <div className="modal" onClick={e => e.stopPropagation()}>
              <div className="modal-header">
                <h2>Bids for Shipment #{modalState.selectedShipment?.shipment_id}</h2>
                <button className="close-btn" onClick={closeBidsModal}>
                  <i className="fas fa-times"></i>
                </button>
              </div>
              <div className="modal-body">
                {modalState.currentBids.length > 0 ? (
                  modalState.currentBids.map((bid, index) => (
                    <div key={getKey(bid, index)} className={`bid-card ${index === 0 ? 'best-bid' : ''}`}>
                      <strong>{bid.driver_id?.name || bid.driver_name || 'Unknown Driver'}</strong>
                      <div>₹{(bid.bid_amount ?? bid.amount ?? 0).toLocaleString()}</div>
                      {bid.message && <div><em>{bid.message}</em></div>}
                      <small>{bid.createdat || bid.created_at ? new Date(bid.createdat || bid.created_at).toLocaleString() : '—'}</small>
                      {bid.status === 'pending' ? (
                        <button>Accept Bid</button>
                      ) : (
                        <span className="badge badge-success">Accepted</span>
                      )}
                    </div>
                  ))
                ) : (
                  <p>No bids available yet</p>
                )}
              </div>
            </div>
          </div>
        )}
      </main>
    </>
  );
}

export default ShipperDashboard;
