import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/shipper-dashboard.css';

function ShipperDashboard() {
  const navigate = useNavigate();

  // ================================
  // STATE MANAGEMENT
  // ================================
  const [currentUser, setCurrentUser] = useState(null);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [theme, setTheme] = useState('dark');
  const [userShipments, setUserShipments] = useState([]);
  const [filteredShipments, setFilteredShipments] = useState([]);
  const [statusFilter, setStatusFilter] = useState('all');

  // Modal states
  const [modalState, setModalState] = useState({
    bidsModal: false,
    tripDetailModal: false,
    chatModal: false,
    ratingModal: false,
    selectedShipment: null,
    currentBids: []
  });

  // Form state
  const [shipmentForm, setShipmentForm] = useState({
    fromLocation: '',
    toLocation: '',
    packageType: '',
    packageWeight: '',
    packageDescription: '',
    vehicleType: '',
    pickupDate: '',
    deliveryDate: '',
    handlingInstructions: ''
  });

  // ================================
  // AUTHENTICATION & INITIALIZATION
  // ================================
  useEffect(() => {
    checkAuthentication();
  }, []);

  const checkAuthentication = () => {
    const token = localStorage.getItem('authToken');
    const userData = localStorage.getItem('userData');

    if (!token || !userData) {
      console.log('âŒ No authentication found');
      navigate('/login');
      return;
    }

    try {
      const user = JSON.parse(userData);
      if (user.role !== 'shipper') {
        console.log('âŒ User is not a shipper');
        navigate('/driver-dashboard');
        return;
      }
      setCurrentUser(user);
      loadUserShipments(user.id);
    } catch (error) {
      console.error('âŒ Auth error:', error);
      navigate('/login');
    }
  };

  // ================================
  // DATA LOADING
  // ================================
  const loadUserShipments = async (userId) => {
    try {
      const response = await fetch(`http://localhost:3000/api/shipments/user/${userId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        }
      });

      if (!response.ok) throw new Error('Failed to fetch shipments');

      const data = await response.json();
      const shipments = data.shipments || [];
      setUserShipments(shipments);
      setFilteredShipments(shipments);
      
      console.log('ðŸ’¾ Loaded shipments:', shipments.length);
    } catch (error) {
      console.error('âŒ Error loading shipments:', error);
    }
  };

  // ================================
  // VIEW BIDS FUNCTIONALITY (FIXED)
  // ================================
  const handleViewBids = async (shipment) => {
    try {
      console.log('ðŸ“‹ Loading bids for shipment:', shipment.shipment_id);
      
      const response = await fetch(
        `http://localhost:3000/api/shipments/${shipment.shipment_id}/bids`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`
          }
        }
      );

      if (!response.ok) {
        throw new Error('Failed to fetch bids');
      }

      const data = await response.json();
      console.log('âœ… Fetched bids:', data.bids);
      
      // Sort bids by amount (lowest first)
      const sortedBids = (data.bids || []).sort((a, b) => a.bidamount - b.bidamount);

      // Update modal state
      setModalState({
        ...modalState,
        bidsModal: true,
        selectedShipment: shipment,
        currentBids: sortedBids
      });

    } catch (error) {
      console.error('âŒ Error loading bids:', error);
      alert('Failed to load bids. Please try again.');
    }
  };

  // ================================
  // ACCEPT BID FUNCTIONALITY (FIXED)
  // ================================
  const handleAcceptBid = async (bid) => {
  // Use window.confirm instead of just confirm
  const confirmed = window.confirm(
    `Accept bid of â‚¹${bid.bidamount.toLocaleString()} from ${bid.drivername}?`
  );
  
  if (!confirmed) {
    return;
  }

  try {
    const response = await fetch(
      `http://localhost:3000/api/bids/${bid.bidid}/accept`,
      {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          shipment_id: modalState.selectedShipment.shipment_id
        })
      }
    );

    if (!response.ok) {
      throw new Error('Failed to accept bid');
    }

    window.alert('Bid accepted successfully!');
    
    // Close modal and refresh data
    setModalState({ ...modalState, bidsModal: false });
    if (currentUser) {
      loadUserShipments(currentUser.id);
    }
    
  } catch (error) {
    console.error('âŒ Error accepting bid:', error);
    window.alert('Failed to accept bid. Please try again.');
  }
};

  // ================================
  // CREATE SHIPMENT
  // ================================
  const handleCreateShipment = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:3000/api/shipments', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          ...shipmentForm,
          shipper_id: currentUser.id
        })
      });

      if (!response.ok) throw new Error('Failed to create shipment');

      alert('Shipment created successfully!');
      setShipmentForm({
        fromLocation: '',
        toLocation: '',
        packageType: '',
        packageWeight: '',
        packageDescription: '',
        vehicleType: '',
        pickupDate: '',
        deliveryDate: '',
        handlingInstructions: ''
      });
      setActiveSection('my-shipments');
      loadUserShipments(currentUser.id);
    } catch (error) {
      console.error('âŒ Error creating shipment:', error);
      alert('Failed to create shipment. Please try again.');
    }
  };

  // ================================
  // FILTER SHIPMENTS
  // ================================
  useEffect(() => {
    if (statusFilter === 'all') {
      setFilteredShipments(userShipments);
    } else {
      setFilteredShipments(userShipments.filter(s => s.status === statusFilter));
    }
  }, [statusFilter, userShipments]);

  // ================================
  // HELPER FUNCTIONS
  // ================================
  const getStatusBadge = (status) => {
    const badges = {
      'pending_bids': { class: 'badge-warning', text: 'Pending Bids' },
      'driver_assigned': { class: 'badge-info', text: 'Driver Assigned' },
      'in_transit': { class: 'badge-primary', text: 'In Transit' },
      'delivered': { class: 'badge-success', text: 'Delivered' },
      'cancelled': { class: 'badge-danger', text: 'Cancelled' }
    };
    const badge = badges[status] || { class: 'badge-secondary', text: status };
    return <span className={`badge ${badge.class}`}>{badge.text}</span>;
  };

  const getVehicleTypeText = (type) => {
    const types = {
      'small': 'Small Vehicle',
      'medium': 'Medium Truck',
      'large': 'Large Truck',
      'xlarge': 'Extra Large Truck'
    };
    return types[type] || type;
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    navigate('/login');
  };

  // ================================
  // RENDER
  // ================================
  if (!currentUser) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className={`shipper-dashboard theme-${theme}`}>
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>ðŸš› CargoMate</h2>
          <p className="user-role">Shipper Portal</p>
        </div>

        <nav className="sidebar-nav">
          <button
            className={activeSection === 'dashboard' ? 'active' : ''}
            onClick={() => setActiveSection('dashboard')}
          >
            <i className="fas fa-home"></i> Dashboard
          </button>
          <button
            className={activeSection === 'create-shipment' ? 'active' : ''}
            onClick={() => setActiveSection('create-shipment')}
          >
            <i className="fas fa-plus"></i> Create Shipment
          </button>
          <button
            className={activeSection === 'my-shipments' ? 'active' : ''}
            onClick={() => setActiveSection('my-shipments')}
          >
            <i className="fas fa-box"></i> My Shipments
          </button>
        </nav>

        <div className="sidebar-footer">
          <div className="user-profile">
            <div className="user-avatar">
              {currentUser.name.charAt(0).toUpperCase()}
            </div>
            <div className="user-info">
              <strong>{currentUser.name}</strong>
              <small>{currentUser.email}</small>
            </div>
          </div>
          <button className="btn-logout" onClick={handleLogout}>
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        {/* Dashboard Section */}
        {activeSection === 'dashboard' && (
          <div className="dashboard-section">
            <h1>Dashboard</h1>
            <div className="stats-grid">
              <div className="stat-card">
                <i className="fas fa-box"></i>
                <h3>Total Shipments</h3>
                <p className="stat-number">{userShipments.length}</p>
              </div>
              <div className="stat-card">
                <i className="fas fa-clock"></i>
                <h3>Pending Bids</h3>
                <p className="stat-number">
                  {userShipments.filter(s => s.status === 'pending_bids').length}
                </p>
              </div>
              <div className="stat-card">
                <i className="fas fa-truck"></i>
                <h3>In Transit</h3>
                <p className="stat-number">
                  {userShipments.filter(s => s.status === 'in_transit').length}
                </p>
              </div>
              <div className="stat-card">
                <i className="fas fa-check-circle"></i>
                <h3>Delivered</h3>
                <p className="stat-number">
                  {userShipments.filter(s => s.status === 'delivered').length}
                </p>
              </div>
            </div>

            <h2>Recent Shipments</h2>
            <div className="shipments-list">
              {userShipments.slice(0, 5).map(shipment => (
                <div key={shipment.shipment_id} className="shipment-card">
                  <div className="shipment-header">
                    <h3>#{shipment.shipment_id}</h3>
                    {getStatusBadge(shipment.status)}
                  </div>
                  <div className="shipment-info">
                    <p><strong>Route:</strong> {shipment.from_location} â†’ {shipment.to_location}</p>
                    <p><strong>Package:</strong> {shipment.package_type} ({shipment.package_weight}kg)</p>
                    <p><strong>Bids:</strong> {shipment.bids_count || 0}</p>
                  </div>
                  {shipment.status === 'pending_bids' && (
                    <button 
                      className="btn btn-primary"
                      onClick={() => handleViewBids(shipment)}
                    >
                      View Bids
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Create Shipment Section */}
        {activeSection === 'create-shipment' && (
          <div className="create-shipment-section">
            <h1>Create New Shipment</h1>
            <form onSubmit={handleCreateShipment} className="shipment-form">
              <div className="form-row">
                <div className="form-group">
                  <label>From Location</label>
                  <input
                    type="text"
                    required
                    value={shipmentForm.fromLocation}
                    onChange={(e) => setShipmentForm({...shipmentForm, fromLocation: e.target.value})}
                    placeholder="Enter pickup location"
                  />
                </div>
                <div className="form-group">
                  <label>To Location</label>
                  <input
                    type="text"
                    required
                    value={shipmentForm.toLocation}
                    onChange={(e) => setShipmentForm({...shipmentForm, toLocation: e.target.value})}
                    placeholder="Enter delivery location"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Package Type</label>
                  <input
                    type="text"
                    required
                    value={shipmentForm.packageType}
                    onChange={(e) => setShipmentForm({...shipmentForm, packageType: e.target.value})}
                    placeholder="e.g., Electronics, Furniture"
                  />
                </div>
                <div className="form-group">
                  <label>Package Weight (kg)</label>
                  <input
                    type="number"
                    required
                    value={shipmentForm.packageWeight}
                    onChange={(e) => setShipmentForm({...shipmentForm, packageWeight: e.target.value})}
                    placeholder="Enter weight"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Package Description</label>
                <textarea
                  required
                  value={shipmentForm.packageDescription}
                  onChange={(e) => setShipmentForm({...shipmentForm, packageDescription: e.target.value})}
                  placeholder="Describe your package"
                  rows="3"
                ></textarea>
              </div>

              <div className="form-group">
                <label>Vehicle Type Required</label>
                <select
                  required
                  value={shipmentForm.vehicleType}
                  onChange={(e) => setShipmentForm({...shipmentForm, vehicleType: e.target.value})}
                >
                  <option value="">Select vehicle type</option>
                  <option value="small">Small Vehicle (up to 500kg)</option>
                  <option value="medium">Medium Truck (500-2000kg)</option>
                  <option value="large">Large Truck (2000-5000kg)</option>
                  <option value="xlarge">Extra Large Truck (5000kg+)</option>
                </select>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Pickup Date</label>
                  <input
                    type="date"
                    required
                    value={shipmentForm.pickupDate}
                    onChange={(e) => setShipmentForm({...shipmentForm, pickupDate: e.target.value})}
                  />
                </div>
                <div className="form-group">
                  <label>Delivery Date</label>
                  <input
                    type="date"
                    required
                    value={shipmentForm.deliveryDate}
                    onChange={(e) => setShipmentForm({...shipmentForm, deliveryDate: e.target.value})}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Handling Instructions (Optional)</label>
                <textarea
                  value={shipmentForm.handlingInstructions}
                  onChange={(e) => setShipmentForm({...shipmentForm, handlingInstructions: e.target.value})}
                  placeholder="Any special instructions for handling"
                  rows="3"
                ></textarea>
              </div>

              <button type="submit" className="btn btn-primary btn-block">
                Create Shipment
              </button>
            </form>
          </div>
        )}

        {/* My Shipments Section */}
        {activeSection === 'my-shipments' && (
          <div className="my-shipments-section">
            <h1>My Shipments</h1>
            
            <div className="filters">
              <button 
                className={statusFilter === 'all' ? 'active' : ''}
                onClick={() => setStatusFilter('all')}
              >
                All
              </button>
              <button 
                className={statusFilter === 'pending_bids' ? 'active' : ''}
                onClick={() => setStatusFilter('pending_bids')}
              >
                Pending Bids
              </button>
              <button 
                className={statusFilter === 'driver_assigned' ? 'active' : ''}
                onClick={() => setStatusFilter('driver_assigned')}
              >
                Driver Assigned
              </button>
              <button 
                className={statusFilter === 'in_transit' ? 'active' : ''}
                onClick={() => setStatusFilter('in_transit')}
              >
                In Transit
              </button>
              <button 
                className={statusFilter === 'delivered' ? 'active' : ''}
                onClick={() => setStatusFilter('delivered')}
              >
                Delivered
              </button>
            </div>

            <div className="shipments-list">
              {filteredShipments.length > 0 ? (
                filteredShipments.map(shipment => (
                  <div key={shipment.shipment_id} className="shipment-card">
                    <div className="shipment-header">
                      <h3>#{shipment.shipment_id}</h3>
                      {getStatusBadge(shipment.status)}
                    </div>
                    <div className="shipment-details">
                      <p><strong>Route:</strong> {shipment.from_location} â†’ {shipment.to_location}</p>
                      <p><strong>Package:</strong> {shipment.package_type} ({shipment.package_weight}kg)</p>
                      <p><strong>Vehicle:</strong> {getVehicleTypeText(shipment.vehicle_type)}</p>
                      <p><strong>Pickup:</strong> {new Date(shipment.pickup_date).toLocaleDateString()}</p>
                      {shipment.bids_count > 0 && (
                        <p><strong>Bids:</strong> {shipment.bids_count} received</p>
                      )}
                      {shipment.selected_driver_name && (
                        <p><strong>Driver:</strong> {shipment.selected_driver_name}</p>
                      )}
                      {shipment.final_amount && (
                        <p><strong>Amount:</strong> â‚¹{shipment.final_amount.toLocaleString()}</p>
                      )}
                    </div>
                    <div className="shipment-actions">
                      {shipment.status === 'pending_bids' && (
                        <button 
                          className="btn btn-primary"
                          onClick={() => handleViewBids(shipment)}
                        >
                          View Bids ({shipment.bids_count || 0})
                        </button>
                      )}
                    </div>
                  </div>
                ))
              ) : (
                <div className="empty-state">
                  <i className="fas fa-box-open"></i>
                  <h3>No Shipments Found</h3>
                  <p>Create your first shipment to get started</p>
                </div>
              )}
            </div>
          </div>
        )}
      </main>

      {/* BIDS MODAL (FIXED VERSION) */}
      {modalState.bidsModal && (
        <div className="modal-overlay" onClick={() => setModalState({ ...modalState, bidsModal: false })}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Bids for Shipment #{modalState.selectedShipment?.shipment_id}</h2>
              <button 
                className="close-btn"
                onClick={() => setModalState({ ...modalState, bidsModal: false })}
              >
                
              </button>
            </div>

            <div className="modal-body">
              {modalState.currentBids && modalState.currentBids.length > 0 ? (
                <div className="bids-list">
                  {modalState.currentBids.map((bid, index) => (
                    <div 
                      key={bid.bidid || bid.bid_id || index}
                      className={`bid-card${index === 0 ? ' best-bid' : ''}`}
                    >
                      {index === 0 && (
                        <div className="best-badge">Best Bid</div>
                      )}
                      
                      <div className="bid-header">
                        <div className="driver-info">
                          <div className="driver-avatar">
                            {bid.drivername ? bid.drivername.charAt(0).toUpperCase() : 'D'}
                          </div>
                          <div>
                            <strong>{bid.drivername || 'Unknown Driver'}</strong>
                            <div className="driver-meta">
                              <span>â­ {bid.driver_rating || 'New Driver'}</span>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="bid-amount">
                        â‚¹{(bid.bidamount || 0).toLocaleString()}
                      </div>

                      <div className="bid-message">
                        <strong>Message:</strong>
                        <p>{bid.message || 'No message provided'}</p>
                      </div>

                      {bid.estimated_delivery_time && (
                        <div className="bid-delivery">
                          <strong>Estimated Delivery:</strong> {bid.estimated_delivery_time}
                        </div>
                      )}

                      <div className="bid-footer">
                        <small>
                          Submitted: {bid.createdat 
                            ? new Date(bid.createdat).toLocaleString() 
                            : 'Unknown'}
                        </small>
                        
                        {bid.status === 'pending' && (
                          <button
                            className="btn btn-sm btn-primary"
                            onClick={() => handleAcceptBid(bid)}
                          >
                            Accept Bid
                          </button>
                        )}
                        
                        {bid.status === 'accepted' && (
                          <span className="badge badge-success">âœ“ Accepted</span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <i className="fas fa-clipboard-list"></i>
                  <h3>No Bids Yet</h3>
                  <p>No drivers have bid on this shipment yet. Please wait for drivers to submit their bids.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ShipperDashboard;
