import React, { useState, useEffect, useRef } from 'react';
import '../styles/tracking-modal.css';

function TrackingModal({ shipment, driver, onClose }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Initial load
  useEffect(() => {
    if (shipment) {
      loadMessages();
    }
  }, [shipment]);

  // Auto-scroll
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const loadMessages = async () => {
    try {
      const token = localStorage.getItem('authToken');
      const response = await fetch(
        `http://localhost:3000/api/messages/${shipment.shipment_id}`,
        {
          headers: { 'Authorization': `Bearer ${token}` }
        }
      );
      
      if (response.ok) {
        const data = await response.json();
        console.log('üì• Messages received:', data);
        
        // Handle both array and object responses
        const msgs = Array.isArray(data.messages) ? data.messages : (data.all_messages || []);
        console.log('üìä Setting messages:', msgs.length, msgs);
        
        setMessages(msgs);
      }
    } catch (error) {
      console.error('‚ùå Error loading:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    
    if (!newMessage.trim()) return;

    const messageText = newMessage;
    setLoading(true);

    try {
      const token = localStorage.getItem('authToken');
      
      const response = await fetch(
        `http://localhost:3000/api/messages/${shipment.shipment_id}`,
        {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            message: messageText.trim(),
            sender_role: 'shipper'
          })
        }
      );

      if (response.ok) {
        const data = await response.json();
        console.log('‚úÖ Response:', data);
        
        // Immediately add message to state
        const newMsg = {
          id: Date.now(),
          shipment_id: shipment.shipment_id,
          sender_role: 'shipper',
          message: messageText.trim(),
          timestamp: new Date().toISOString()
        };
        
        setMessages(prev => [...prev, newMsg]);
        setNewMessage('');
        
        // Also reload from server to sync
        setTimeout(loadMessages, 300);
      }
    } catch (error) {
      console.error('‚ùå Send Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="tracking-modal-overlay" onClick={onClose}>
      <div className="tracking-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="tracking-modal-header">
          <h2>üí¨ Chat with Driver</h2>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="tracking-container">
          {/* Left: Info */}
          <div className="tracking-section">
            <h3>üìç Trip Info</h3>
            <div className="shipment-info">
              <div className="info-row">
                <span className="label">ID:</span>
                <span className="value">{shipment.shipment_id}</span>
              </div>
              <div className="info-row">
                <span className="label">Route:</span>
                <span className="value">{shipment.from_location} ‚Üí {shipment.to_location}</span>
              </div>
              <div className="info-row">
                <span className="label">Driver:</span>
                <span className="value">{driver?.name || 'Assigned'}</span>
              </div>
              <div className="info-row">
                <span className="label">Status:</span>
                <span className={`status-badge ${shipment.status}`}>
                  {shipment.status?.toUpperCase()}
                </span>
              </div>
            </div>

            <div className="live-map">
              <div className="map-icon">üó∫Ô∏è</div>
              <p>Tracking Map</p>
              <div className="tracking-badge">
                <div className="pulse"></div> Live
              </div>
            </div>
          </div>

          {/* Right: Chat */}
          <div className="chat-section">
            <h3>üí¨ Messages ({messages.length})</h3>
            
            <div className="messages-container">
              {messages.length === 0 ? (
                <div className="no-messages">Start chatting!</div>
              ) : (
                messages.map((msg, idx) => (
                  <div key={idx} className={`message ${msg.sender_role}`}>
                    <div className="message-content">
                      <p>{msg.message}</p>
                      <small>
                        {msg.timestamp ? new Date(msg.timestamp).toLocaleTimeString() : ''}
                      </small>
                    </div>
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            <form onSubmit={sendMessage} className="message-form">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
                disabled={loading}
              />
              <button type="submit" disabled={loading}>
                {loading ? '‚è≥' : 'üì§'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TrackingModal;
